from typing import List
from datetime import datetime
from collections import deque

# Track execution time of the function
def timeit(func):
    def wrapper(*args, **kwargs):
        start = datetime.now()
        value = func(*args, **kwargs)
        end = datetime.now()
        print(f"Time: {end-start}")
        return value
    return wrapper


# https://leetcode.com/problems/jump-game/description/
class Solution:

    @timeit
    # First intuitive solution - slow one
    def canJump(self, nums: List[int]) -> bool:

        # Init array to save number of ways to reach to the specific point
        num_count = len(nums)
        dp = [0] * len(nums)
        dp[0] = 1
        
        for i in range(len(nums)):
            # Point should be reachable
            if i > 0 and dp[i] == 0:
                return False           
            # Iterate throuh all availalble steps
            for step in range(1, nums[i] + 1):
                dp_idx = i + step
                if dp_idx >= num_count:
                    break
                dp[dp_idx] += 1
           
        return True
    
    @timeit
    # Second - optimized without unuseful calculations
    def canJump2(self, nums: List[int]) -> bool:
        max_idx = 0
        for i in range(len(nums)):
            if max_idx < i:
                return False
            max_idx = max(max_idx, nums[i] + i)
        return True
    

def judge(result, expected):
    print(f'Result {result} Expected {expected}')
    assert result == expected
    

